
public class Mainclass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		Operator : 연산자
		사칙연산자
		++ increment
		-- decrement
		
		 */

		int number1, number2;
		int result;
		
		number1 = 25;
		number2 = 7;
		
		result = number1 + number2;
		System.out.println(result);
		
		result = number1 - number2;
		System.out.println(result);
		
		result = number1 * number2;
		System.out.println(result);
		
		result = number1 / number2;
		System.out.println(result);
		
		
		//Arithmetic == 산수, 연산
	}

}
